import Modal from "../UI/Modal";
import React, { useContext } from "react";
import "./Cart.css";
import itemContext from "../store/ContexStore";
import CartItem from "./CartItem";

const Cart = () => {
  const { showCart, setShowCart } = useContext(itemContext);

  const cartCtx = useContext(itemContext);
  console.log(cartCtx, "pppppppp");
  const Total = `${cartCtx.totalAmount?.toFixed(2)}`;

  // const cartItemRemoveHandler = (id) => {
  //   cartCtx.removeItem(id);
  // };
  const handlePopUp = () => {
    setShowCart(false);
  };

  return (
    showCart && (
      <Modal>
        <div className="cart-item">
          <div>
            {cartCtx?.items?.map((item) => {
              <div key={item.id}>
                <div>{item.name}</div>
                <img src={item?.image} className="image_1" alt="burgar" />
              </div>;
            })}
          </div>

          <div className="total">
            <span>Total Amount</span>
            <span>${Total}</span>
          </div>
          <div className="actions">
            <button className="button_clo" onClick={handlePopUp}>
              Close
            </button>
            <button className="button_ord">Order</button>
          </div>
        </div>
      </Modal>
    )
  );
};

export default Cart;
