import "./FoodItem.css";
import Form from "./Form";
import Card from "../UI/Card";
import items from "./Items";
import itemContext from "../store/ContexStore";
import { useContext } from "react";

const FoodItems = (props) => {
  const cartCtx = useContext(itemContext);

  const price=`$${props.price.toFixed(2)}`;

    const addToCartHandler=(amount)=>{
        cartCtx.addItem({
          id:props.id,
          name:props.name,
          amount:amount,
          price:props.price
        })
        console.log('here item added to cart',cartCtx);

    }
  return (
    <Card>
      {items?.map((item) => (
        <div className="list" key={item.id}>
          <div>
            <img src={item?.image} className="image_1" alt="burgar" />

            <li className="first_list">{item?.name}</li>
            <li className="second_list">{item?.origin}</li>
            <li className="third_list"> ${item?.price}</li>
          </div>
          <div className="form">
            <Form id={props.id} item={props} addToCartHandler={addToCartHandler} />
            {console.log(props.id, "lllllll")}
          </div>
        </div>
      ))}
    </Card>
  );
};

export default FoodItems;
