import "./FoodItem.css";
import Form from "./Form";
import Card from "../UI/Card";

import itemContext from "../store/ContexStore";
import { useContext } from "react";

const FoodItems = (props) => {
  const cartCtx=useContext(itemContext);
  const items = [
    {
      id: 1,
      image:
        "https://playswellwithbutter.com/wp-content/uploads/2022/06/Best-Ever-Grilled-Chicken-Burgers-18.jpg",
      name: "Chicken Burgar",
      origin: "Chinise-Food",
      price: 20,
  
    },
    {
      id: 2,
      image:
        "https://vismaifood.com/storage/app/uploads/public/ef5/cc0/637/thumb__1200_0_0_0_auto.jpg",
      name: "Chicken Pakoda",
      origin: "North-Indian",
      price: 25,
  
    },
    {
      id: 3,
      image:
        "https://geekrobocook.com/wp-content/uploads/2021/05/Muradabadi-chicken-biryani-1200x900.jpg",
      name: "Hyderbad chicken Biryani",
      origin: "Hyderbad",
      price: 50
     
    },
    {
      id: 4,
      image: "https://i.ytimg.com/vi/JqP4wSWsYr4/maxresdefault.jpg",
      name: "Krishnam-Veg",
      origin: "South-indian",
      price: 10,
  
    },
    {
      id: 5,
      image:
        "https://www.indianetzone.com/photos_gallery/203/1_full_course_udupi_meal.jpg",
      name: "Udupi-Food",
      origin: "Karnataka",
      price: 15,
  
    },
  ];
  
  


  const addToCartHandler=(amount)=>{
    cartCtx.addItem({
      id:props.id,
      name:props.name,
      amount:amount,
      price:props.price
    })
  }


  return (
    <Card>
      {props.items?.map((item) => (
        <div className="list" key={item.id}>
          <div>
            <img src={item?.image} className="image_1" alt="burgar" />

            <li className="first_list">{item?.name}</li>
            <li className="second_list">{item?.origin}</li>
            <li className="third_list"> ${item?.price}</li>
          </div>
          <div className="form">
            <Form id={props.id} item={items}/>
            {console.log(props.id,"lllllll")}
          </div>
        </div>
      ))}
    </Card>
  );
};

export default FoodItems;
