import React, { Fragment, useContext } from "react";

import Cart from "../Cart/Cart";
import CartIcon from "../Cart/CartIcon";
import { itemContext } from "../store/Context";
import "./HederCartButton.css";

const HeaderCartButton = () => {
  const { showCart, setShowCart } = useContext(itemContext);

const {cartItems} = useContext(itemContext)

const numberOfItems = cartItems.reduce((curNum, item) => {
  return curNum+item.amount
}, 0)

  const handlePopUp = () => {
    setShowCart(true);
  };

  return (
    <Fragment>
      {showCart && <Cart />}
      <button className="button" onClick={handlePopUp}>
        <span className="icon">
          <CartIcon />
        </span>
        <span>Your Cart</span>
        <span className="badge">{numberOfItems}</span>
      </button>
    </Fragment>
  );
};

export default HeaderCartButton;
