import Modal from "../UI/Modal";
import React, { useContext } from "react";
import "./Cart.css";
import itemContext from "../store/ContexStore";

const Cart = () => {
  const { showCart, setShowCart } = useContext(itemContext);

  const cartCtx = useContext(itemContext);
  const Total = `${cartCtx.totalAmount.toFixed(2)}`;

  // const cartItemRemoveHandler = (id) => {
  //   cartCtx.removeItem(id);
  // };
  const handlePopUp = () => {
    setShowCart(false);
  };

  const cartItem = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.quantity}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
        />
      ))}
    </ul>
  );

  return (
    showCart && (
      <Modal>
        <div className="cart-item">
          {cartCtx.items?.map((item) => (
            <div key={item?.id}>
              <div>{item?.name}</div>
              <div>{item.quantity}</div>
              <div>{item.price}</div>
            </div>
          ))}
          <div className="total">
            <span>Total Amount</span>
            <span>{Total}</span>
          </div>
          <div className="actions">
            <button className="button_clo" onClick={handlePopUp}>
              Close
            </button>
            <button className="button_ord">Order</button>
          </div>
        </div>
      </Modal>
    )
  );
};

export default Cart;
